import { StyleSheet, Text , View ,Image,FlatList, Pressable} from 'react-native'
import React , {useState} from 'react'

import SeeMoreButton from './customComponent/SeeMoreButton';
import { SafeAreaView } from 'react-native-safe-area-context';


export default function PopularProduct() {
    const [product , setProduct]=useState([
        {
            source :require ('../assets/product/s22.png'), 
            name:'Samsung S22 5G 256/8',
            price:'3999 DT'
        },
        {
            source :require ('../assets/product/iphone13.png'), 
            name:'iPhone 13 5G 256/4',
            price:'3999 DT'
        },
        {
            source :require ('../assets/product/vega32.png'), 
            name:'Televion Vega 32 pouce',
            price:'699 DT'
        },
        {
            source :require ('../assets/product/toshibaS25.png'), 
            name:'Televion Toshiba 32 pouce',
            price:'599 DT'
        },
        {
            source :require ('../assets/product/infinixXe22.png'), 
            name:'Earbuds Infinix XE 22',
            price:'59 DT'
        },
        {
            source :require ('../assets/product/toshibaS25.png'), 
            name:'iPhone 13 5G 256/4',
            price:'3999 DT'
        },
        {
            source :require ('../assets/product/toshibaS25.png'), 
            name:'iPhone 13 5G 256/4',
            price:'3999 DT'
        },
    ]);
    
  return (
    <>
        <Text style={styles.title} >Popular Product</Text>
        <FlatList  numColumns={2}
            data={product}
             renderItem={({item}) => (
          <Pressable   style={styles.container}>
            <Image source={item.source} style={styles.product} />
            <Text  style={styles.name}> {item.name} </Text>
            <Text  style={styles.price}> {item.price} </Text>
            <View style={{alignItems:'flex-end'}}>
            <SeeMoreButton/>
            </View>
            </Pressable>
            
)}
    
    
    />
    </>
  )
}

const styles = StyleSheet.create({
    container:{
        flex:1,
        backgroundColor:'#fff',
        margin:5,
        borderRadius:20
    },
    title:{
        fontSize:15,
        color:'#4361ee',
        fontWeight:'bold',
        margin:5
    },
    product:{
        resizeMode:'contain',
        width:170,    
        height:170,
        justifyContent:'center',
        alignItems:'center',
        alignSelf:'center'
    },  
    price:{
        fontSize:15,
        fontWeight:'bold',
        alignItems:'flex-end',
        justifyContent:'flex-end',
        marginTop:15,
        textAlign:'right',
        color:'red'
        
    },
    name:{
        textAlign:'center',
        color:'#4361ee',
        fontSize:15
    }
    

})
