import React from "react";
import  {StyleSheet} from "react-native"
const styles =StyleSheet .create({
    container:{
        flex:1,
        backgroundColor:'#e9ecef',

    },
    containerSign:{
        flex:1,
        marginTop:25,
        margin:10,
        justifyContent:'center'
    },
    title:{
        fontSize:35,
        textAlign:'center',
        justifyContent:'center',
        color:'#0077b6',
        marginBottom:5,
        fontWeight: 'bold',
        textDecorationLine:'underline'
    },
    input:{
        backgroundColor:'#ced4da',
        borderBottomWidth:2,
        borderColor:'#3f37c9',
        borderRadius:10,
        color:'#343a40',
        padding:15,
        fontSize:15,
        margin:10,
    },
    errText:{
        fontSize:10,
        color:'red',
        textAlign:'center',
        marginBottom:5    
    },
    text:{
        marginTop:10,
        textAlign:'center',
        color:'#4361ee'
    },
    sign:{
        
        resizeMode:'cover'    
    },
    viewimg:{justifyContent:'center',
    alignContent:'center'}
   
})
export default styles;
