import { StyleSheet, Text, View,Image,FlatList ,Animated,Dimensions} from 'react-native'
import React, {useState,useEffect} from 'react'
import { Items } from '../data/data';

export default function productInfo({route,navigation}) {
  
  const { item } = route.params;
  const [product, setProduct] = useState({});
  useEffect(() => {
      const unsubscribe = navigation.addListener('focus', () => {
      getDataFromDB();
        });
      return unsubscribe;}, [navigation]
  );

  const getDataFromDB =  () => {
      for (let index = 0; index < Items.length; index++) {
        if (Items[index]== item) {
          setProduct(Items[index]);
          return;
      }
    }
  };
  const scrollX = new Animated.Value(0);

  const width = Dimensions.get('window').width;

  const renderProduct = ({item, index}) => {
    return (
      <View
        style={{
          width: width,
          height: 240,
          alignItems: 'center',
          justifyContent: 'center',
        }}>
        <Image
          source={item}
          style={{
            width: '100%',
            height: '100%',
            resizeMode: 'contain',
          }}
        />
      </View>
    );
  };
  





  
  return (
    <View>

       
    <FlatList  style={styles.productImageList}
            data={product.productImageList ? product.productImageList : null}
            horizontal
            renderItem={renderProduct}
            showsHorizontalScrollIndicator={false}
            decelerationRate={0.8}
            bounces={false}
            onScroll={Animated.event(
              [{nativeEvent: {contentOffset: {x: scrollX}}}],
              {useNativeDriver: false},
            )}
          />
  
          <View  style={styles.container}   >
          <View style={{alignContent:'space-between'}} >
            <Text style={styles.productName} > {product.productName}</Text>
            <Text style={styles.productPrice} > {product.productPrice} </Text> 
          </View>
          <Text style={styles.description} > {product.description} </Text>
          

          </View>
    </View>
  )
}

const styles = StyleSheet.create({
  container :{
    backgroundColor:'#fff',
    borderTopLeftRadius:50,
    borderTopRightRadius:50,
    width:'100%',
    height:'60%',
    alignSelf:'center',
    shadowColor: 'black',
    shadowOffset: { width: 5, height: 2 },
    shadowRadius: 5,  
    elevation  : 10,

  },
  productImageList:{
    marginBottom:15,
    marginTop:15,
    height:'40%'
  },
  productName:{
    fontSize:25,
  },
  description:{ 
    textAlign:'center',
    alignItems:'center',
    

  },
  productPrice:{
    textAlign:'left',
    fontSize:25,
    color:'red',
  }


})
