import { Image, ScrollView, StyleSheet, Text, View } from 'react-native'
import React from 'react'
import { navigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import CategoriesList from "./customComponent/CategoriesList";
import AsyncStorage from '@react-native-async-storage/async-storage';



const Stack = createNativeStackNavigator();
export default function MyCartScreen({route,navigation}) {
  const { product } = route.params;
  const getUser = async () => {
    try {
      const getProductData = JSON.parse(await AsyncStorage.getItem("productData"))
    } catch (error) {
     console.log(error); 
    }
  };
  return (
      <View style={styles.container} >
        <ScrollView style={styles.productContainer} >
          <View style={styles.product} >
        <Image source={product.productImage} style={styles.productImage} />
       <View style={styles.productsDetails} >
        <Text   > {product.category} </Text>
        <Text > {product.productName}  </Text>
        <Text> {product.productPrice} </Text>
        </View>
        </View>
        
        </ScrollView>

        <View  >
 <Text > {productData}  </Text>

        </View>


    </View>
  )
}

const styles = StyleSheet.create({
  container:{

  },
  productContainer:{
    height:'70%',

  },
  product:{
    flexDirection:'row',
  
  },
  productImage:{
    resizeMode:'contain',
    width:'40%'
  },
  productsDetails:{
    marginTop:25
  }




})
