import React from "react";
import {
    View ,Button, TextInput,Text,
    TouchableOpacity
} from "react-native"

import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';

import styles from "../styles/global";
import { Formik } from "formik";
import * as yup from 'yup';

const validation = yup.object({
    fullName:yup.string()
    .required("Enter your name")
    .matches(/(\w.+\s).+/, 'Enter at least 2 names')
    .min(3),
    email:yup
    .string()
    .email("Please enter valid email")
    .required("Email adress is required"),
    password:yup
    .string()
    .min(8)
    .required("Password is required"),
    confirmPassword: yup
    .string()
    .oneOf([yup.ref('password')], 'Passwords do not match')
    .required('Confirm password is required'),
})


const SignUp=({NA}) =>{

    return (
       
        <View style={styles.container} > 
                     
            <Formik
            initialValues={{fullName:'',email :'' , password:'',confirmPassword:''} }
            validationSchema={validation}
            onSubmit={(values,actions) => {
                actions.resetForm()
                console.log(values)



            }}
            >
                {({handleChange,
     handleSubmit,
     values,
     errors,
     })=>( 
                    <View style={styles.containerSign}>
                       <Text style={styles.title}> Register
                         </Text>  
                        <TextInput 
                        style={styles.input}
                        placeholder='Full name' 
                        onChangeText={handleChange('fullName')}
                        value={values.fullName}
                        />
                        {errors.fullName &&
                        <Text style={styles.errText}>{errors.fullName}</Text>
       }
                        <TextInput
                         style={styles.input}
                        placeholder='Email adress' 
                        keyboardType="email-address"
                        onChangeText={handleChange('email')}
                        value={values.email}
                        />
                        {errors.email &&
                        <Text style={styles.errText}>{errors.email}</Text>
                        }
                        <TextInput 
                        style={styles.input}
                        secureTextEntry={true}
                        placeholder='Password' 
                        onChangeText={handleChange('password')}
                        value={values.password}
                        />
                        {errors.password &&
                        <Text style={styles.errText}>{errors.password}</Text>
                        }
                        <TextInput 
                        style={styles.input}
                        placeholder='Confirm your password' 
                        secureTextEntry={true}
                        onChangeText={handleChange('confirmPassword')}
                        value={values.confirmPassword}
                        />
                        {errors.confirmPassword &&
                        <Text style={styles.errText}>{errors.confirmPassword}</Text>
                        }
                        <Button
                        title="Register now"
                        color='#560bad'
                        onPress={handleSubmit}
                        />
                        <TouchableOpacity >
                        <Text style={styles.text}>Already have an account Sign in </Text>
                        </TouchableOpacity>

                    </View>
                    
                    
                )}
            </Formik>
        </View>

    )

} 
export default SignUp;
