import { StyleSheet,  View, Image,FlatList , Text,TouchableOpacity } from 'react-native'
import React from 'react'

const CategoriesList = () =>{
  const categoriesData=[
    {
      categoryName :'phone',
    source:require('../../assets/categories/phone.jpg'),
  },
  {
    categoryName:'computer',
    source:require('../../assets/categories/computer.png'),
  },
  {
    categoryName:'tv',
    source:require('../../assets/categories/tv.png'),
  },
  {
    categoryName:'earbuds',
    source :require('../../assets/categories/earbuds.png'),
    
  },
  {
    categoryName:'keyboard',
    source:require('../../assets/categories/keyboard.png')
  },
  { 
    categoryName:'mouse',
    source: require('../../assets/categories/mouse.png')
  },
  {
    categoryName:'charger',
    source: require('../../assets/categories/charger.png')
  }

]
  const getCategoryName= ()=>{}
    return(
            <>
            <FlatList horizontal 
            showsHorizontalScrollIndicator={false}
              data={categoriesData}
              renderItem={({item}) => (
                <TouchableOpacity >
                <Image  style={styles.ImgCategories} source={item.source}/>
                </TouchableOpacity>
              )}
              />
            

        </>
    )


}

export default function Categories() {
  return (
    <View style={styles.container} >
      <CategoriesList/>
    </View>
  )
}

const styles = StyleSheet.create({
    text:{
      fontSize:20,
      padding:5,
      color:'#0077b6',
      width:'30%',
      textAlign:'center',
      borderRadius:5,
      fontWeight:'bold'
     

        },
    
    ImgCategories:{
      
      backgroundColor:'#fff',
      resizeMode:'contain',
      width:60,
      height:60,
      alignItems:'center',
      alignSelf:'center',
      margin:5,
      borderRadius:15
      
    }
})
